<style>
	aside, .index, span {
		transition: all 200ms ease;
	}
	span {
		white-space: nowrap;
	}
	aside {
		width: 240px;
		padding: 12px;
		gap: 24px;
		background: var(--neutral-200);
	}
	.index {
		color: var(--neutral-800) !important;
		font-weight: 700;
		font-size: 20px;
	}
	.index, .option {
		padding: 8px 12px;
	}
	header > button {
		padding: 8px;
		border-radius: 8px;
	}
	nav {
		gap: 12px;
		flex: 1;
	}
	.option {
		width: 100%;		
		gap: 8px;
		border-radius: 8px;
		color: var(--neutral-500);
	}
	.option:hover, .opt-active, header > button:hover {
		color: var(--neutral-800);
		background: var(--neutral-300);
	}
	a:hover {
		text-decoration: none;
	}
	svg {
		flex-shrink: 0;
	}

	/* Sidebar minimizada */
	.mini {
		width: 72px;
	}
	.mini .index, .mini .option > span {
		display: none;
	}
</style>